package com.example.sychiprpclibmcc.rpc;import java.io.PrintWriter;import java.io.StringWriter;/** *  * Title: LogDefault<br> * Description:<br> * Copyright: SyChip Electronic Technology (Shanghai) Ltd.A subsidiary of * muRata. All Rights Reserved. <br> *  * @author SyChip * @version 0.0.01 * */public class LogDefault implements LogIf {		/**	 * log error	 * 	 * @param param	 *            true or false	 */	public void logErr(Object param){		System.err.println(param.toString());	}	/**	 * log out	 * 	 * @param param	 *            true or false	 */	@Override	public void logOut(Object param) {		System.out.println(param.toString());	}	@Override	public void logExecption(Exception e) {		try{		StringWriter sw = new StringWriter();		  e.printStackTrace(new PrintWriter(sw));			System.err.println(sw.toString());		}catch(Exception e1){					}	};	}